/**
 * Returns accompaniments for a given main recipeId, filtered by allowed kinds if provided.
 * @param recipeId Main dish RecipeId
 * @param kinds Optional filter for MealComponentKind (e.g., ['SIDE'])
 */

export function getAccompanimentsForRecipe(
  recipeId: RecipeId,
  kinds?: MealComponentKind[]
): { kind: MealComponentKind; recipeId: RecipeId }[] {
  const all = BOOK_ACCOMPANIMENTS[recipeId] || [];
  if (!kinds) return all.slice();
  return all.filter(a => kinds.includes(a.kind));
}

// Legacy/test aliases for compatibility
export const getBookAccompaniments = getAccompanimentsForRecipe;
export function hasBookAccompaniments(recipeId: RecipeId): boolean {
  return (BOOK_ACCOMPANIMENTS[recipeId]?.length ?? 0) > 0;
}

